func:
global.set('1isPressed',0);
global.set('2isPressed',0);
global.set('3isPressed',0);
global.set('4isPressed',0);

global.set('1',0);
global.set('2',0);
global.set('3',0);
global.set('4',0);

global.set('gameOver',0);
global.set('numPressed',0);

var mine = Math.floor(Math.random() * 4 + 1);
global.set(""+mine,1);
msg.payload = mine;
return msg;

var gameState = global.get('gameOver');
if(gameState === 1){
    msg.payload = "The Game is Over! Please Reset!";
     return msg;
}


var pressed = msg.payload;
var check = global.get(pressed+"isPressed"); 


if(check === 0){ //has not been pressed
    var press = global.get('numPressed');
    global.set('numPressed',press+1);

    global.set(pressed+"isPressed",1);

    var isMine = global.get(""+pressed);
    if (isMine === 1){
        msg.payload = "Mineeeeeee!";
        global.set('gameOver',1);
        return msg;
    }else{
        if(press < 1){ //2
        msg.payload = "You are fine, but you must press another button";
        }else{
         msg.payload = "You win!!!!!!!!!!!!!!!!!!";   
        }
        return msg;
    }

}
